import React from "react";
import { useRecoilState } from "recoil";
import { AddressData } from "../../assets/atoms/main.recoil";
import AmountDetailsCard from "../Amount-Details-Card/amount-details-card";

const MyAddress = () => {
  const [addressText, setAddressText] = useRecoilState(AddressData);

  return (
    <>
      <form>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Address
          </label>
          <input
            type="text"
            className="form-control"
            value={addressText.address}
            readOnly
          />
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Phone
          </label>
          <input
            type="text"
            className="form-control"
            value={`${addressText.code}${addressText.phone}`}
            readOnly
          />
        </div>
      </form>
      <AmountDetailsCard />
    </>
  );
};

export default MyAddress;
