import React from "react";
import { useRecoilState, useRecoilValue } from "recoil";
import {
  AddProductItems,
  FilterSearchProduct,
  SearchProduct,
} from "../../assets/atoms/main.recoil";
import HeaderComponent from "../../header";
import ProductCard from "./product-card";

const ProductList = () => {
  const addProductAtom = useRecoilValue(FilterSearchProduct);

  const [searchText, setSearchText] = useRecoilState(SearchProduct);

  return (
    <>
      <HeaderComponent />

      <div className="search-box my-5">
        <h1>Product List</h1>

        <div className="mb-3">
          <label className="form-label">Product Type</label>
          <select
            class="form-select"
            aria-label="Default select example"
            // onChange={handleChange}
            name="type"
            // value={values.type}
          >
            <option selected>Select product type</option>
            <option value="kindle">Kindle</option>
            <option value="hardcover">Hardcover</option>
            <option value="paperback">Paperback</option>
          </select>

          {/* {touched.type && errors.type && (
          <div className="alert alert-danger" role="alert">
            {errors.type}
          </div>
        )} */}
        </div>
        <div className="serach-input">
          <input
            className="form-control"
            type="text"
            placeholder="Search product"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
          />
        </div>
      </div>

      {addProductAtom.length > 0 ? (
        <div className="product-list-main">
          {addProductAtom.map((productitem) => {
            return (
              <ProductCard productitem={productitem} key={productitem.id} />
            );
          })}
        </div>
      ) : (
        <h5 className="text-center my-5">No product find!</h5>
      )}
    </>
  );
};

export default ProductList;
